import React, { Component } from 'react';
import { Card, ListGroup, ListGroupItem } from 'react-bootstrap'
export default class ContactList extends Component {

    constructor(props) { 
        super(props);

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(item) { 
        if (this.props.onContactSelect) {
            this.props.onContactSelect(item);
        }
    }

    render() {
        return (

                <Card className="card" style={{ width: '16rem' }}>
                    <span className="title">Favorites </span>

                    { this.props.favorites && this.props.favorites.map(contact => {
                        return (
                            <ListGroup variant="flush">
                            <ListGroup.Item className="select" key={contact.id} >
                                 <div className="userSmall"></div>

                                 <div>        
                                     <div className="favorite"></div>
                                <a  onClick={ () => this.handleClick(contact) }> <span className="userName">{contact.name}</span>
                                <span className="subTitle">{contact.companyName }</span>
                                </a>

                                </div>
                             </ListGroup.Item>
                        </ListGroup>
                    )
                        })
                    }

                    <span className="title">Others</span>
                    { this.props.others && this.props.others.map(contact => {
                        return (
                            <ListGroup variant="flush">
                          <ListGroup.Item className="select" key={contact.id}  > 
                            <div className="userSmall"></div>
                                 <div className="noFavorite"></div>
                                 <a className = "select">
                                      <span className="userName" onClick={ () => this.handleClick(contact) }>{contact.name} </span> 
                                 </a>
                            </ListGroup.Item>
                        </ListGroup>
                        )
                    })}
                    </Card>


        );
    }
}
