import React, { Component } from 'react';
import 'flexboxgrid'
export default class ContactDetails extends Component {

    constructor(props) { 
        super(props);
    }

    goBack() {
        if (this.props.goBack) {
            this.props.goBack();
        }
    }

    render() {
        return (
            <div>
                <button onClick={ () => this.goBack() } ><span> Contacts </span></button>
                { this.props.selectedContact && 
                    <div class="row">
                        <div class="col-xs-12
                                    col-sm-8
                                    col-md-6
                                    col-lg-4">
                        <div class="box">
                        <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={ this.props.selectedContact.largeImageURL} />
                        <Card.Body>
                            <Card.Title>{ this.props.selectedContact.name } </Card.Title>
                            <Card.Text>
                            { this.props.selectedContact.companyName }
                            </Card.Text>
                        </Card.Body>
                        <ListGroup className="list-group-flush">
                            <ListGroupItem> </ListGroupItem>
                            <ListGroupItem> </ListGroupItem>
                            <ListGroupItem> </ListGroupItem>
                        </ListGroup>
                        <Card.Body>
                            <Card.Link href="#"> </Card.Link>
                            <Card.Link href="#"></Card.Link>
                        </Card.Body>
                        </Card>



                        </div>
                    </div>
                    </div>
                }
            </div>
        );
    }
}
