import React, { Component } from 'react';
import { Card, ListGroup, ListGroupItem } from 'react-bootstrap'
export default class ContactList extends Component {

    constructor(props) { 
        super(props);

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(item) { 
        if (this.props.onContactSelect) {
            this.props.onContactSelect(item);
        }
    }

    render() {
        return (

                <Card className="card" style={{ width: '16rem' }}>
                    <span>Favorites</span>

                    { this.props.favorites && this.props.favorites.map(contact => {
                        return (
                            <ListGroup variant="flush">
                            <ListGroup.Item key={contact.id} onClick={ () => this.handleClick(contact) } > <span className="favorite"></span>{contact.name}

                             </ListGroup.Item>
                        </ListGroup>
                    )
                        })
                    }


                    <span>Others</span>
                    { this.props.others && this.props.others.map(contact => {
                        return (
                            <ListGroup variant="flush">
                            <ListGroup.Item key={contact.id} onClick={ () => this.handleClick(contact) } > {contact.name} </ListGroup.Item>
                        </ListGroup>
                        )
                    })}
                    </Card>


        );
    }
}
