import React, { Component } from 'react';
import 'flexboxgrid'
import { Card, ListGroup, ListGroupItem } from 'react-bootstrap'
export default class ContactDetails extends Component {

    constructor(props) { 
        super(props);
    }

    goBack() {
        if (this.props.goBack) {
            this.props.goBack();
        }
    }

    render() {
        return (
            <div class="row">

                { this.props.selectedContact && 
                        <div class="col-xs-12
                                    col-sm-8
                                    col-md-6
                                    col-lg-4">
                        <div class="box">
                        <a class="back" onClick={ () => this.goBack() } ><span> Contacts </span></a>
                        <Card className="card" style={{ width: '14rem' }}>
                        <Card.Img variant="top" src={ this.props.selectedContact.largeImageURL} />
                        <Card.Body>
                            <Card.Title>{ this.props.selectedContact.name } </Card.Title>
                            <Card.Text>
                            { this.props.selectedContact.companyName }
                            </Card.Text>
                        </Card.Body>
                        <ListGroup className="list-group-flush">
                            <ListGroupItem>       { this.props.selectedContact.companyName } </ListGroupItem>
                            <span> Phone </span>
                            <ListGroupItem> { this.props.selectedContact.phone.home } <span> Home </span></ListGroupItem>
                            <span> Phone </span>
                            <ListGroupItem> { this.props.selectedContact.phone.mobile } <span> Mobile</span></ListGroupItem>
                            <span> Phone </span>
                            <ListGroupItem> { this.props.selectedContact.phone.work } <span>Work </span> </ListGroupItem>
                            <span> Address </span>
                            <ListGroupItem> { ` ${this.props.selectedContact.address.street},  ${this.props.selectedContact.address.city },  ${this.props.selectedContact.address.state }  ${this.props.selectedContact.address.zipCode },  ${this.props.selectedContact.address.country} `}</ListGroupItem>
                            <span> Birthdate </span>
                            <ListGroupItem> { this.props.selectedContact.birthdate }</ListGroupItem>
                            <span> Email </span>
                            <ListGroupItem> { this.props.selectedContact.email }</ListGroupItem>
                        </ListGroup>
                        <Card.Body>
                            <Card.Link href="#"> </Card.Link>
                            <Card.Link href="#"></Card.Link>
                        </Card.Body>
                        </Card>




                    </div>
                    </div>
                }
            </div>
        );
    }
}
